const mongoose = require\('mongoose'\);
require\('dotenv'\).config\(\);

async function run\(\) {
  await mongoose.connect\(process.env.MONGODB_URL\);
  const db = mongoose.connection.db;

  // 1. Find user by name containing 'ronen'
  const user = await db.collection\('users'\).findOne\({
    $or: [{ firstName: /ronen/i }, { email: /ronen/i }]
  }\);
  console.log\('=== 1. USER FOUND ==='\);
  console.log\('_id:', user?._id\);
  console.log\('firstName:', user?.firstName\);
  console.log\('lastName:', user?.lastName\);
  console.log\('email:', user?.email\);
  console.log\('company:', user?.company?.name || user?.companyName\);

  if \(!user\) {
    console.log\('No user found. Exiting.'\);
    await mongoose.disconnect\(\);
    return;
  }

  const userId = user._id;

  // 2. Count total inventory items for that seller
  const totalCount = await db.collection\('inventory'\).countDocuments\({ sellerId: userId }\);
  console.log\('\\n=== 2. TOTAL INVENTORY COUNT ==='\);
  console.log\('Total items for sellerId \(ObjectId\):', totalCount\);

  const totalCountStr = await db.collection\('inventory'\).countDocuments\({ sellerId: userId.toString\(\) }\);
  console.log\('Total items for sellerId \(string\):', totalCountStr\);

  // 3. Search for 'cisco' across key text fields for this user
  const ciscoItems = await db.collection\('inventory'\).find\({
    sellerId: userId,
    $or: [
      { manufacturer: /cisco/i },
      { partNumber: /cisco/i },
      { description: /cisco/i },
      { model: /cisco/i }
    ]
  }\).limit\(5\).toArray\(\);
  console.log\('\\n=== 3. CISCO SEARCH \(this user, ObjectId sellerId\) ==='\);
  console.log\('Found:', ciscoItems.length\);
  ciscoItems.forEach\(\(item, i\) => {
    console.log\('Item', i + 1, ':', JSON.stringify\({
      manufacturer: item.manufacturer,
      partNumber: item.partNumber,
      model: item.model,
      description: item.description?.substring\(0, 80\)
    }, null, 2\)\);
  }\);

  // Try with string sellerId too
  const ciscoItemsStr = await db.collection\('inventory'\).find\({
    sellerId: userId.toString\(\),
    $or: [
      { manufacturer: /cisco/i },
      { partNumber: /cisco/i },
      { description: /cisco/i },
      { model: /cisco/i }
    ]
  }\).limit\(5\).toArray\(\);
  console.log\('\\n=== 3a. CISCO SEARCH \(this user, string sellerId\) ==='\);
  console.log\('Found:', ciscoItemsStr.length\);
  ciscoItemsStr.forEach\(\(item, i\) => {
    console.log\('Item', i + 1, ':', JSON.stringify\({
      manufacturer: item.manufacturer,
      partNumber: item.partNumber,
      model: item.model,
      description: item.description?.substring\(0, 80\)
    }, null, 2\)\);
  }\);

  // Search across all users
  const ciscoAll = await db.collection\('inventory'\).find\({
    $or: [
      { manufacturer: /cisco/i },
      { partNumber: /cisco/i },
      { description: /cisco/i },
      { model: /cisco/i }
    ]
  }\).limit\(5\).toArray\(\);
  console.log\('\\n=== 3b. CISCO SEARCH ALL USERS ==='\);
  console.log\('Found across all users:', ciscoAll.length\);
  ciscoAll.forEach\(\(item, i\) => {
    console.log\('Item', i + 1, ':', JSON.stringify\({
      sellerId: item.sellerId?.toString\(\),
      manufacturer: item.manufacturer,
      partNumber: item.partNumber,
      model: item.model
    }, null, 2\)\);
  }\);

  // 4. Sample 3 inventory items for this user
  let sampleItems = await db.collection\('inventory'\).find\({ sellerId: userId }\).limit\(3\).toArray\(\);
  console.log\('\\n=== 4. SAMPLE INVENTORY ITEMS \(ObjectId sellerId\) ==='\);
  console.log\('Count:', sampleItems.length\);

  if \(sampleItems.length === 0\) {
    sampleItems = await db.collection\('inventory'\).find\({ sellerId: userId.toString\(\) }\).limit\(3\).toArray\(\);
    console.log\('Retrying with string sellerId, found:', sampleItems.length\);
  }

  sampleItems.forEach\(\(item, i\) => {
    console.log\('\\nItem', i + 1, '- All field names:', Object.keys\(item\).join\(', '\)\);
    console.log\(JSON.stringify\(item, null, 2\)\);
  }\);

  await mongoose.disconnect\(\);
}

run\(\).catch\(err => {
  console.error\('Error:', err\);
  process.exit\(1\);
}\);
SCRIPT_EOF)
